from django.test import TestCase

from practice.services import MNIST


class MNISTTestCase(TestCase):

    def testBasicTraining(self):
        mnist = MNIST()
        mnist.load_data(test=False)
        mnist.set_algorithm(model_type='Single layer', weight_initialize='No', activation_function='Sigmoid', dropout='No')
        mnist.set_training('GradientDescentOptimizer', 0.01, 10)
        mnist.run()

    def testAdvancedTraining(self):
        mnist = MNIST()
        mnist.load_data(test=False)
        mnist.set_algorithm(model_type='Multiple layers', weight_initialize='Yes', activation_function='ReLU', dropout='No')
        mnist.set_training('GradientDescentOptimizer', 0.01, 10)
        mnist.run()

    def testTestAll(self):
        mnist = MNIST()
        mnist.load_data(test=True)
        mnist.test_all(model_type='Multiple layers', weight_initialize='Yes', activation_function='ReLU', dropout='No')

